function burgerhiden(){const e=document.querySelector(".burger"),t=document.querySelector(".hiden"),r=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),r.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerhiden(),window.addEventListener("scroll",fixedNav);const readMoreBtn=document.querySelector("#read-more-btn"),text=document.querySelector(".service__text");readMoreBtn.addEventListener("click",(e=>{text.classList.toggle("show-more"),"READ MORE"===readMoreBtn.innerText?readMoreBtn.innerText="READ LESS":readMoreBtn.innerText="READ MORE"}));